/***                         function 开始                         ***/
function MainView() {
  $ui.render({
    props: {
      id: "Main",
      title: "ACPlayer TF V" + Version
    },
    views: [{
      type: "input",
      props: {
        id: "inputView",
        type: $kbType.search,
        align: $align.center,
        clearsOnBeginEditing: true,
        placeholder: "请输入关键字"
      },
      layout: function (make, view) {
        make.width.equalTo(screenWidth - 20)
        make.top.equalTo(view.prev).inset(3)
        make.height.equalTo(32)
        make.left.inset(10)
      },
      events: {
        returned: function (sender) {
          if (sender.text) {
            sender.blur()
            if ($("menu").index) {
              isQQ = true
            } else {
              isQQ = false
            }
            inputtext = encodeURI(sender.text)
            getURL(returnAPI($("menu").index) + inputtext)
          } else {
            initWithSwich()
          }
        }
      }
    }, {
      type: "menu",
      props: {
        items: ["网易云", "QQ音乐"]
      },
      layout: function (make, view) {
        make.left.right.equalTo(0)
        make.top.equalTo(view.prev.bottom).offset(3)
        make.height.equalTo(44)
      },
      events: {
        changed: function (sender) {
          if (sender.index) {
            isQQ = true
          } else {
            isQQ = false
          }
          if (inputtext)
            getURL(returnAPI(sender.index) + inputtext)
        }
      }
    }, {
      type: "list",
      props: {
        id: "menuList",
        rowHeight: 100.0,
        separatorInset: $insets(0, 5, 0, 0),
        template: [{
          type: "image",
          props: {
            id: "image"
          },
          layout: function (make, view) {
            make.left.top.bottom.inset(5)
            make.width.equalTo(view.height)
          }
        },
        {
          type: "label",
          props: {
            id: "title",
            font: $font("bold", 18),
            autoFontSize: true
          },
          layout: function (make) {
            var preView = $("image")
            make.left.equalTo(preView.right).offset(10)
            make.height.equalTo(18)
            make.top.inset(8)
            make.right.inset(5)
          }
        },
        {
          type: "label",
          props: {
            id: "id",
            font: $font(12),
            bgcolor: $color("#F5F5F5"),
            textColor: $color("#666666"),
            radius: 2
          },
          layout: function (make) {
            var preView = $("title")
            make.top.equalTo(preView.bottom).offset(5)
            make.height.equalTo(20)
            make.left.equalTo(preView.left)
          }
        },
        {
          type: "label",
          props: {
            id: "singer",
            font: $font(12),
            textColor: $color("#666666")
          },
          layout: function (make) {
            var preView = $("id")
            make.top.equalTo(preView.bottom).offset(5)
            make.left.equalTo(preView.left)
            make.right.inset(5)
          }
        },
        {
          type: "label",
          props: {
            id: "flac",
            font: $font(12),
            textColor: $color("#666666")
          },
          layout: function (make) {
            var preView = $("singer")
            make.top.equalTo(preView.bottom).offset(5)
            make.left.equalTo(preView.left)
            make.right.inset(5)
          }
        },
        ],
        actions: [{
          title: "视频",
          handler: function (tableView, indexPath) {
            var object = tableView.object(indexPath)
            if (object.flac.mvurl) {
              $audio.pause()
              $("Main").add({
                type: "blur",
                props: {
                  alpha: 0,
                  style: 2,
                  id: "mvPage",
                },
                layout: $layout.fill,
                views: [{
                  type: "scroll",
                  props: {
                    radius: 5,
                    id: "infoPage",
                    bgcolor: $color("white"),
                    showsVerticalIndicator: false,
                  },
                  layout: function (make, view) {
                    width = screenWidth - 20
                    height = width * 0.75 + 50
                    make.center.equalTo(view.super)
                    make.size.equalTo($size(width, height))
                  },
                  views: [{
                    type: "video",
                    props: {
                      src: object.flac.mvurl
                    },
                    layout: function (make, view) {
                      width = screenWidth - 30
                      height = width * 0.75
                      make.top.equalTo(5)
                      make.centerX.equalTo(view.super)
                      make.size.equalTo($size(width, height))
                    }
                  }, {
                    type: "label",
                    props: {
                      text: "下拉视频关闭"
                    },
                    layout: function (make, view) {
                      make.centerX.equalTo(view.super)
                      make.top.equalTo(view.prev.bottom).offset(10)
                    }
                  }],
                  events: {
                    pulled: function (sender) {
                      $("video").pause()
                      $("infoPage").endRefreshing()
                      $ui.animate({
                        duration: 1,
                        animation: function () {
                          $("mvPage").alpha = 0
                        },
                        completion: function () {
                          $audio.resume()
                        }
                      })
                    }
                  }
                }]
              })
              $ui.animate({
                duration: 1,
                animation: function () {
                  $("mvPage").alpha = 1
                },
                completion: function () {
                  $("video").play()
                }
              })
            } else {
              $ui.toast("没有这个视频的MV哦~")
            }
          }
        }, {
          title: "分享",
          handler: function (tableView, indexPath) {
            var object = tableView.object(indexPath)
            if (isQQ) {
              url = player + "str=" + inputtext + "&id=" + object.id.id
            } else {
              url = player + object.id.id
            }
            $share.sheet([object.image, url, object.title.text])
          }
        },
        {
          title: "打开",
          handler: function (tableView, indexPath) {
            var object = tableView.object(indexPath)
            if (isQQ) {
              url = player + "str=" + inputtext + "&id=" + object.id.id
            } else {
              url = player + object.id.id
            }
            $app.openURL(url)
          }
        },
        {
          title: "下载",
          handler: function (tableView, indexPath) {
            var object = tableView.object(indexPath)
            downloader(object)
          }
        }
        ]
      },
      layout: function (make, view) {
        make.left.right.equalTo(0)
        make.top.equalTo(view.prev.bottom)
        make.bottom.inset($device.info.model == "iPhone10,3" ? 60 : 44)
      },
      events: {
        didSelect: function (tableView, indexPath) {
          cellData = tableView.object(indexPath)
          PlayIndex = indexPath.row
          fileExit(splicPath(cellData.title.text, cellData.singer.singer, "mp3"), 2)
          musicPlay(cellData)
          pushMusicPage(cellData)
        },
        pulled: function (sender) {
          getURL(keyword)
        }
      }
    }, BottomVIEW]
  })
  ChangeColor($("B1"), 0)
  $thread.background({
    delay: 0,
    handler: function () {
      if ($cache.get("cache"))
        $("menuList").data = $cache.get("cache")
    }
  })
}

function SecondView() {
  tempdata = []
  tempdata.push({
    name: {
      text: "未完成等待开发",
    },
    type: {
      text: "Design by AbleCats",
    }
  })
  $ui.render({
    props: {
      id: "PathList",
      title: "Mine"
    },
    views: [{
      type: "menu",
      props: {
        id: "listMenu",
        index: 1,
        items: ["试听列表", "离线歌曲"]
      },
      layout: function (make, view) {
        make.height.equalTo(44)
        make.top.left.right.equalTo(0)
      },
      events: {
        changed: function (sender) {
          switch (sender.index) {
            case 0:
              data = tempdata
              break;
            case 1:
              data = updataLocalList()
              break;
          }
          $("listPage").data = data
        }
      }
    }, {
      type: "list",
      props: {
        id: "listPage",
        rowHeight: 50.0,
        selectable: false,
        data: updataLocalList(),
        template: [{
          type: "label",
          props: {
            id: "name",
            lines: 0,
            font: $font(16),
            autoFontSize: true,
            align: $align.left
          },
          layout: function (make, view) {
            make.top.equalTo(5)
            make.height.equalTo(20)
            make.right.left.inset(10)

          }
        }, {
          type: "label",
          props: {
            id: "type",
            font: $font(12),
            align: $align.left
          },
          layout: function (make, view) {
            make.height.equalTo(25)
            make.right.left.inset(15)
            make.top.equalTo(view.prev).offset(20)
          }
        }],
        actions: [
          {
            title: "删除",
            handler: function (sender, indexPath) {
              var object = sender.object(indexPath)
              if (object)
                deleteMusic(object.path)
              sender.data = updataLocalList()
            }
          },
          {
            title: "分享",
            handler: function (sender, indexPath) {
              var object = sender.object(indexPath)
              file = $file.read(object.path)
              name = object.name.shared
              if (object)
                $share.sheet([name, file])
            }
          },
          {
            title: "播放",
            handler: function (sender, indexPath) {
              var object = sender.object(indexPath)
              if (object) {
                $audio.play({ path: object.path })
              }
            }
          },
        ]
      },
      layout: function (make, view) {
        make.left.right.equalTo(0)
        make.top.equalTo(view.prev.bottom)
        make.bottom.inset($device.info.model == "iPhone10,3" ? 60 : 44)
      },
      events: {
        pulled(sender) {
          switch ($("listMenu").index) {
            case 0:
              data = tempdata
              break;
            case 1:
              data = updataLocalList()
              break;
          }
          sender.data = data
          $("listPage").endRefreshing()
        }
      }
    }, BottomVIEW]
  })
  ChangeColor($("B2"), 1)
}

