function transTime(value) {
  function formatTime(value) {
    var time = "";
    var s = value.split(':');
    var i = 0;
    for (; i < s.length - 1; i++) {
      time += s[i].length == 1 ? ("0" + s[i]) : s[i];
      time += ":";
    }
    time += s[i].length == 1 ? ("0" + s[i]) : s[i];

    return time;
  }

  var time = "";
  var h = parseInt(value / 3600);
  value %= 3600;
  var m = parseInt(value / 60);
  var s = parseInt(value % 60);
  if (h > 0) {
    time = formatTime(h + ":" + m + ":" + s);
  } else {
    time = formatTime(m + ":" + s);
  }

  return time;
}

function killTimer() {
  timer.invalidate()
}

function getLrc(url) {
  $http.get({
    url: url,
    handler: function (res) {
      parseLyric(res.data)
    }
  })
}

function parseLyric(lrc) {
  LRC = []
  var lrcObj = {};
  var lyrics = lrc.split("\n");
  for (var i = 0; i < lyrics.length; i++) {
    var lyric = decodeURIComponent(lyrics[i]);
    var timeReg = /\[\d*:\d*((\.|\:)\d*)*\]/g;
    var timeRegExpArr = lyric.match(timeReg);
    if (!timeRegExpArr) continue;
    var clause = lyric.replace(timeReg, '');
    for (var k = 0, h = timeRegExpArr.length; k < h; k++) {
      var t = timeRegExpArr[k];
      var min = Number(String(t.match(/\[\d*/i)).slice(1)),
        sec = Number(String(t.match(/\:\d*/i)).slice(1));
      var time = min * 60 + sec;
      if (clause)
        lrcObj[time] = clause;
    }
  }
  if(lrcObj){
    for (let i in lrcObj) {
      LRC.push({
        index: i,
        lrc: {
          text: lrcObj[i]
        }
      })
    }
  }
  $("lrcPage").data = LRC
}

function getURL(api) {
  $("menuList").beginRefreshing()
  $ui.loading(true)
  $ui.toast("数据正在获取中,请稍后...")
  $http.get({
    url: api,
    handler: function (res) {
      updata(res.data)
    }
  })
}

function updata(stories) {
  var data = []
  $cache.remove("cache")
  let count = stories.count
  for (var id = 0; id < count; id++) {
    story = stories.data[id]
    data.push({
      url: story.url,
      lrc: story.lrc,
      image: {
        src: story.pic
      },
      id: {
        id: story.id,
        text: story.id + platform()
      },
      title: {
        text: story.title
      },
      singer: {
        singer: story.singer,
        text: '歌手:' + story.singer
      },
      flac: {
        url: story.flac,
        mvurl: story.mvurl,
        text: '无损:' + getTure(story.flac) + "  MV:" + getTure(story.mvurl),
      }
    })
  }
  $ui.loading(false)
  $cache.set("cache", data)
  $("menuList").data = data
  $("menuList").endRefreshing()
}

function musicPlay(object) {
  $ui.loading(true)
  path = splicPath(object.title.text, object.singer.singer, "mp3")
  if (playinfo == null || $audio.status == 0) {
    playinfo = object
    if ($file.exists(path)) {
      $audio.play({
        path: path
      })
      $ui.toast('文件已存在，使用本地文件播放...')
    }
    else {
      $audio.play({
        url: object.url
      })
      $ui.toast('歌曲正在缓冲,请稍后...')
    }
    $thread.background({
      delay: 0,
      handler: function () {
        addPlayeingPage()
        getLrc(object.lrc)
      }
    })
  }
}

function playToggle() {
  if (alignment()) {
    if ($audio.status == 2) {
      $audio.pause()
      timer.invalidate()
      $ui.loading(false)
      removePlayeingPage()
    }
    else if ($audio.status == 0) {
      getLrc(cellData.lrc)
      $audio.resume()
      $ui.loading(false)
      addPlayeingPage()
    }
  }
  else {
    killTimer()
    $audio.stop()
    removePlayeingPage()
    musicPlay(cellData)
  }
}

function isPlaying() {
  if (alignment()) {
    if ($audio.status != 0)
      return true
    else
      return false
  }
}

function platform() {
  let platform
  if (isQQ) {
    platform = ' | QQ'
  } else {
    platform = ' | WY'
  }
  return platform
}

function isWY() {
  if (keyword == undefined || keyword.indexOf('music.163.com') == -1) {
    return true
  } else {
    return false
  }
}

function getTure(s) {
  if (s) {
    return 'TRUE'
  } else {
    return 'FALSE'
  }
}

function fileExit(object, statu) {
  switch (statu) {
    case 1:
      if (!$file.exists(object)) {
        $file.mkdir(object)
      }
      break;
    case 2:
      if (!$file.exists(object) || isDownload == object) {
        hideDelete = true
        hideDownload = false
      }
      else {
        hideDelete = false
        hideDownload = true
      }
      break;
  }

}

function saveMusic(data, path, object) {
  $file.write({
    data: data,
    path: path
  })
  isDownload = null
  $ui.toast("下载成功,歌曲已在本地保存")
}

function deleteMusic(path) {
  if ($file.exists(path)) {
    $file.delete(path)
    $ui.toast("歌曲文件已删除...")
  }

}

function downloader(object) {
  if (!isDownload) {
    if (!object.flac.url)
      items = ["高清"]
    else
      items = ["高清", "无损"]
    $ui.menu({
      items: items,
      handler: function (title, idx) {
        type = 'mp3'
        url = object.url
        if (idx) {
          url = object.flac.url
          path = type = 'flac'
        }
        path = splicPath(object.title.text, object.singer.singer, type)
        if ($file.exists(path))
          $ui.toast("当前歌曲已下载...")
        else {
          isDownload = object
          $http.download({
            url: url,
            progress: function (bytesWritten, totalBytes) {
              var percentage = bytesWritten * 1.0 / totalBytes
            },
            handler: function (resp) {
              saveMusic(resp.data, path, object)
            }
          })
        }
      }
    })
  }
  else
    $ui.toast("当前已有下载任务,请稍后...")
}

function openURL(url) {
  $ui.push({
    props: {
      title: url
    },
    views: [{
      type: "web",
      props: {
        url: url
      },
      layout: $layout.fill
    }]
  })
}

function initConf() {

  fileExit(LOCAL, 1)
  fileExit(ICLOUD, 1)
  fileExit('AbleCatsPlayer', 1)

  if (typeof (Favoritefile) == "undefined") {
    Favoritefile = $file.read(ICLOUD + "Favorite.conf")
    if (typeof (Favoritefile) == "undefined")
      FAVORITE = 0
    else
      FAVORITE = 1;
  }
  else FAVORITE = 1

  if (typeof (PathDatafile) == "undefined") {
    PathDatafile = $file.read(ICLOUD + "Favorite.conf")
    if (typeof (PathDatafile) == "undefined") {
      PathData = 0
    }

    else
      PathData = 1
  }
  else
    PathData = 1

  FAVORITE_FILE = FAVORITE ? JSON.parse(Favoritefile.string) : JSON.parse(JSON.stringify(DEFAULT_FAVORITE))
  PATHDATA_FILE = PathData ? JSON.parse(PathDatafile.string) : JSON.parse(JSON.stringify(DEFAULT_PATHDATA))
}

function findPathSame(path) {
  for (const key in PATHDATA_FILE) {
    file = PATHDATA_FILE[key]
    if (file.name == path) {
      return PATHDATA_FILE[key].data
    }
  }
  return false
}

function unique(id) {
  var file = JSON.stringify(FAVORITE_FILE)
  return file.indexOf(id) == -1 ? true : false
}

