function updataVersion() {

  function datainfo(data) {
    var info = ''
    if (data) {
      for (const key in data) {
        info = info + "- " + data[key] + "\n"
      }
    }
    return info
  }
  $http.get({
    url: "http://js.able.cat/ACPlayerTF/updata/",
    handler: function (resp) {
      var data = resp.data
      info = datainfo(data.updatainfo)
      if (data.version > Version) {
        $ui.alert({
          title: "检测到新的版本！V" + data.version,
          message: "是否更新?\n更新完成后自行启动新版本\n\n" + info + "\n" + data.tips,
          actions: [{
            title: "更新",
            handler: function () {
              var url = "jsbox://install?url=http://js.able.cat/ACPlayer/ACPlayer%20TF.js&name=ACPlayerTF&icon=icon_048.png";
              $app.openURL(encodeURI(url));
              $app.close()
            }
          }, {
            title: "取消"
          }]
        })
      }
    }
  })
}

